import React from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const PasswordRoute = ({ children }) => {
  const { passwordVerified, isAuthenticated } = useAuth();

  // If password is not verified, redirect to password protection page
  if (!passwordVerified) {
    return <Navigate to="/password-protection" replace />;
  }

  // If password is verified but user is not authenticated, redirect to login
  if (passwordVerified && !isAuthenticated) {
    return <Navigate to="/login" replace />;
  }

  // If both password is verified and user is authenticated, render the children
  return children;
};

export default PasswordRoute;
