import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ScrollingBanner = () => {
  const [banner, setBanner] = useState({
    text: 'Coach Connect 2025-2026, Coach Camps, Coach Summit (Spring)',
    color: '#28b6ea',
    hyperlink: '',
    isActive: true
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBanner = async () => {
      try {
        const res = await axios.get('/api/banner');
        setBanner(res.data);
      } catch (error) {
        console.error('Error fetching banner:', error);
        // Use default banner on error
      } finally {
        setLoading(false);
      }
    };

    fetchBanner();
  }, []);

  if (loading || !banner.isActive) {
    return null;
  }

  const bannerContent = banner.hyperlink ? (
    <a
      href={banner.hyperlink}
      target="_blank"
      rel="noopener noreferrer"
      style={{
        color: 'inherit',
        textDecoration: 'none',
        display: 'inline-block',
        padding: '0 50px'
      }}
    >
      {banner.text}
    </a>
  ) : (
    <span style={{ padding: '0 50px', display: 'inline-block' }}>
      {banner.text}
    </span>
  );

  return (
    <div
      style={{
        width: '100%',
        overflow: 'hidden',
        background: banner.color,
        color: 'white',
        padding: '12px 0',
        marginBottom: '2rem',
        position: 'relative',
        whiteSpace: 'nowrap'
      }}
    >
      <div
        className="scrolling-text"
        style={{
          display: 'inline-block',
          animation: 'scroll 30s linear infinite',
          fontSize: '1rem',
          fontWeight: '600',
          textTransform: 'uppercase',
          letterSpacing: '1px'
        }}
      >
        {bannerContent}
        {bannerContent} {/* Duplicate for seamless loop */}
      </div>
      <style>
        {`
          @keyframes scroll {
            0% {
              transform: translateX(0);
            }
            100% {
              transform: translateX(-50%);
            }
          }
        `}
      </style>
    </div>
  );
};

export default ScrollingBanner;
