import React, { useState } from 'react';
import { FaPlay } from 'react-icons/fa';

const VideoCard = ({ video, onClick }) => {
  const [imageError, setImageError] = useState(false);

  const handleClick = () => {
    onClick(video._id);
  };

  // Convert Google Drive URL to embed URL
  const getEmbedUrl = (driveUrl) => {
    const fileId = driveUrl.match(/\/d\/(.+?)(\/|$)/)?.[1];
    if (fileId) {
      return `https://drive.google.com/file/d/${fileId}/preview`;
    }
    return driveUrl;
  };

  // Generate thumbnail from Google Drive URL
  const getThumbnailUrl = () => {
    // If we have a custom thumbnail URL, use it
    if (video.thumbnailUrl) {
      return video.thumbnailUrl;
    }

    // Try to generate thumbnail from Google Drive URL
    const fileId = video.googleDriveUrl?.match(/\/d\/(.+?)(\/|$)/)?.[1];
    if (fileId) {
      // Use Google Drive's thumbnail service
      return `https://drive.google.com/thumbnail?id=${fileId}&sz=w300-h200`;
    }

    // Fallback to a simple colored background with text
    return null;
  };

  // Handle image load error
  const handleImageError = () => {
    setImageError(true);
  };

  // Create a fallback thumbnail with video info
  const renderFallbackThumbnail = () => {
    const categoryColors = {
      'workout-sports': '#FF6B35',
      'dance-move': '#4ECDC4',
      'yoga': '#45B7D1',
      'mindfulness': '#96CEB4',
      'pre-school': '#FFEAA7',
      'k-12': '#DDA0DD'
    };

    const bgColor = categoryColors[video.category] || '#2c3e50';

    return (
      <div 
        style={{
          width: '100%',
          height: '100%',
          background: `linear-gradient(135deg, ${bgColor}, ${bgColor}dd)`,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          padding: '1rem',
          textAlign: 'center',
          color: 'white',
          fontSize: '0.9rem',
          fontWeight: '600'
        }}
      >
        <div style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>
          {video.category === 'yoga' ? 'ðŸ§˜' : 
           video.category === 'workout-sports' ? 'ðŸ’ª' :
           video.category === 'dance-move' ? 'ðŸ’ƒ' :
           video.category === 'mindfulness' ? 'ðŸ§ ' :
           video.category === 'pre-school' ? 'ðŸ‘¶' : 'ðŸŽ“'}
        </div>
        <div style={{ fontSize: '0.8rem', opacity: 0.9 }}>
          {video.category.replace('-', ' ').toUpperCase()}
        </div>
      </div>
    );
  };

  return (
    <div className="video-card" onClick={handleClick}>
      <div className="video-thumbnail">
        {!imageError && getThumbnailUrl() ? (
          <img 
            src={getThumbnailUrl()} 
            alt={video.title}
            onError={handleImageError}
            style={{ width: '100%', height: '100%', objectFit: 'cover' }}
          />
        ) : (
          renderFallbackThumbnail()
        )}
        <div className="play-button">
          <FaPlay />
        </div>
      </div>
      <div className="video-info">
        <h3 className="video-title">{video.title}</h3>
        <div className="video-meta">
          {video.instructor && (
            <p>Instructor: {video.instructor}</p>
          )}
          {video.duration && (
            <p>Duration: {Math.floor(video.duration / 60)}:{(video.duration % 60).toString().padStart(2, '0')}</p>
          )}
          <p>Views: {video.viewCount || 0}</p>
          {video.difficulty && (
            <span 
              style={{
                background: video.difficulty === 'beginner' ? '#2ecc71' : 
                           video.difficulty === 'intermediate' ? '#f39c12' : '#e74c3c',
                padding: '2px 8px',
                borderRadius: '10px',
                fontSize: '0.8rem',
                textTransform: 'capitalize'
              }}
            >
              {video.difficulty}
            </span>
          )}
        </div>
      </div>
    </div>
  );
};

export default VideoCard;
