import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useVideo } from '../contexts/VideoContext';
import { useAuth } from '../contexts/AuthContext';
import { FaArrowLeft, FaArrowRight, FaTimes } from 'react-icons/fa';

const VideoPlayer = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { currentVideo, getVideo, trackVideoView, videos } = useVideo();
  const { isAuthenticated } = useAuth();
  const [currentVideoIndex, setCurrentVideoIndex] = useState(0);

  useEffect(() => {
    const loadVideo = async () => {
      const video = await getVideo(id);
      if (video && isAuthenticated) {
        try {
          await trackVideoView(id);
        } catch (error) {
          console.error('Error tracking video view:', error);
        }
      }
    };
    loadVideo();
  }, [id, isAuthenticated]);

  useEffect(() => {
    if (videos.length > 0) {
      const index = videos.findIndex(video => video._id === id);
      setCurrentVideoIndex(index >= 0 ? index : 0);
    }
  }, [id, videos]);

  const handleClose = () => {
    navigate('/');
  };

  const handlePrevious = () => {
    if (currentVideoIndex > 0) {
      const prevVideo = videos[currentVideoIndex - 1];
      navigate(`/video/${prevVideo._id}`);
    }
  };

  const handleNext = () => {
    if (currentVideoIndex < videos.length - 1) {
      const nextVideo = videos[currentVideoIndex + 1];
      navigate(`/video/${nextVideo._id}`);
    }
  };

  const getEmbedUrl = (driveUrl) => {
    const fileId = driveUrl.match(/\/d\/(.+?)(\/|$)/)?.[1];
    if (fileId) {
      return `https://drive.google.com/file/d/${fileId}/preview`;
    }
    return driveUrl;
  };

  if (!currentVideo) {
    return (
      <div className="loading">
        Loading video...
      </div>
    );
  }

  return (
    <div style={{ 
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      width: '100vw',
      height: '100vh',
      backgroundColor: '#000',
      zIndex: 1000,
      overflow: 'auto'
    }}>
      {/* Back Button */}
      <button
        onClick={handleClose}
        style={{
          position: 'absolute',
          top: '1rem',
          left: '1rem',
          background: 'rgba(0, 0, 0, 0.7)',
          border: 'none',
          borderRadius: '8px',
          padding: '0.75rem 1rem',
          color: 'white',
          cursor: 'pointer',
          display: 'flex',
          alignItems: 'center',
          gap: '0.5rem',
          zIndex: 1001,
          fontSize: '1rem',
          fontWeight: '600',
          transition: 'all 0.3s ease'
        }}
        onMouseEnter={(e) => {
          e.currentTarget.style.background = 'rgba(0, 0, 0, 0.9)';
        }}
        onMouseLeave={(e) => {
          e.currentTarget.style.background = 'rgba(0, 0, 0, 0.7)';
        }}
      >
        <FaArrowLeft /> Back
      </button>

      {/* Navigation Buttons */}
      {videos.length > 1 && (
        <>
          <button
            onClick={handlePrevious}
            disabled={currentVideoIndex === 0}
            style={{
              position: 'absolute',
              left: '1rem',
              top: '50%',
              transform: 'translateY(-50%)',
              background: currentVideoIndex === 0 ? 'rgba(255, 255, 255, 0.2)' : 'rgba(255, 107, 53, 0.9)',
              border: 'none',
              borderRadius: '50%',
              width: '50px',
              height: '50px',
              color: 'white',
              cursor: currentVideoIndex === 0 ? 'not-allowed' : 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              zIndex: 1001,
              transition: 'all 0.3s ease'
            }}
            onMouseEnter={(e) => {
              if (currentVideoIndex !== 0) {
                e.currentTarget.style.background = 'rgba(255, 107, 53, 1)';
                e.currentTarget.style.transform = 'translateY(-50%) scale(1.1)';
              }
            }}
            onMouseLeave={(e) => {
              if (currentVideoIndex !== 0) {
                e.currentTarget.style.background = 'rgba(255, 107, 53, 0.9)';
                e.currentTarget.style.transform = 'translateY(-50%) scale(1)';
              }
            }}
          >
            <FaArrowLeft />
          </button>

          <button
            onClick={handleNext}
            disabled={currentVideoIndex === videos.length - 1}
            style={{
              position: 'absolute',
              right: '1rem',
              top: '50%',
              transform: 'translateY(-50%)',
              background: currentVideoIndex === videos.length - 1 ? 'rgba(255, 255, 255, 0.2)' : 'rgba(255, 107, 53, 0.9)',
              border: 'none',
              borderRadius: '50%',
              width: '50px',
              height: '50px',
              color: 'white',
              cursor: currentVideoIndex === videos.length - 1 ? 'not-allowed' : 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              zIndex: 1001,
              transition: 'all 0.3s ease'
            }}
            onMouseEnter={(e) => {
              if (currentVideoIndex !== videos.length - 1) {
                e.currentTarget.style.background = 'rgba(255, 107, 53, 1)';
                e.currentTarget.style.transform = 'translateY(-50%) scale(1.1)';
              }
            }}
            onMouseLeave={(e) => {
              if (currentVideoIndex !== videos.length - 1) {
                e.currentTarget.style.background = 'rgba(255, 107, 53, 0.9)';
                e.currentTarget.style.transform = 'translateY(-50%) scale(1)';
              }
            }}
          >
            <FaArrowRight />
          </button>
        </>
      )}

      {/* Video Content - Full Screen */}
      <div style={{ 
        width: '100%',
        height: '100vh',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '2rem'
      }}>
        {/* Video Player - Full Screen */}
        <div style={{ 
          position: 'relative', 
          width: '100%', 
          maxWidth: '100%',
          height: 'calc(100vh - 200px)',
          minHeight: '500px',
          background: '#000',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center'
        }}>
          <iframe
            src={getEmbedUrl(currentVideo.googleDriveUrl)}
            width="100%"
            height="100%"
            frameBorder="0"
            allowFullScreen
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            title={currentVideo.title}
            style={{
              border: 'none'
            }}
          />
        </div>

        {/* Video Info - Below Video */}
        <div style={{ 
          width: '100%',
          maxWidth: '1200px',
          marginTop: '2rem',
          padding: '0 2rem',
          color: 'white'
        }}>
          <h1 style={{ 
            marginBottom: '1rem', 
            color: 'white',
            fontSize: '1.5rem',
            fontWeight: '600'
          }}>
            {currentVideo.title}
          </h1>

          <div style={{ 
            display: 'flex', 
            flexWrap: 'wrap', 
            gap: '1.5rem', 
            fontSize: '0.95rem',
            marginBottom: '1rem',
            color: 'rgba(255, 255, 255, 0.8)'
          }}>
            {currentVideo.instructor && (
              <span>
                <strong>Instructor:</strong> {currentVideo.instructor}
              </span>
            )}
            {currentVideo.duration && (
              <span>
                <strong>Duration:</strong> {Math.round(currentVideo.duration / 60)} minutes
              </span>
            )}
            <span>
              <strong>Views:</strong> {currentVideo.viewCount || 0}
            </span>
            {videos.length > 1 && (
              <span>
                <strong>Video {currentVideoIndex + 1} of {videos.length}</strong>
              </span>
            )}
          </div>

          {currentVideo.description && (
            <p style={{ 
              lineHeight: '1.6',
              color: 'rgba(255, 255, 255, 0.9)',
              marginTop: '1rem'
            }}>
              {currentVideo.description}
            </p>
          )}
        </div>
      </div>
    </div>
  );
};

export default VideoPlayer;
