import React from 'react';

const SubcategoryTabs = ({ activeCategory, activeSubcategory, onSubcategoryChange }) => {
  const subcategories = [
    { id: 'pre-k-2', name: 'Pre - K - 2' },
    { id: 'grades-3-4', name: 'Grades 3 - 4' },
    { id: 'grades-5-8', name: 'Grades 5 - 8' },
    { id: 'high-school', name: 'High School' }
  ];

  // Get the color based on active category
  const getCategoryColor = () => {
    switch (activeCategory) {
      case 'workout-sports':
        return '#f7981e';
      case 'dance-move':
        return '#4ECDC4';
      case 'yoga':
        return '#96CEB4';
      case 'mindfulness':
        return '#45B7D1';
      case 'pre-school':
        return '#FFEAA7';
      case 'k-12':
        return '#DDA0DD';
      default:
        return '#FF6B35';
    }
  };

  const categoryColor = getCategoryColor();

  return (
    <div style={{
      position: 'absolute',
      top: '100%',
      left: '0',
      zIndex: 10,
      background: categoryColor,
      borderRadius: '15px',
      padding: '1rem',
      boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)',
      minWidth: '250px',
      marginTop: '10px'
    }}>
      <div style={{ 
        color: 'white', 
        fontWeight: 'bold', 
        marginBottom: '0.5rem',
        fontSize: '0.9rem'
      }}>
        5 Minute Videos
      </div>

      <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
        {subcategories.map((subcategory) => (
          <button
            key={subcategory.id}
            onClick={() => onSubcategoryChange(subcategory.id)}
            style={{
              background: activeSubcategory === subcategory.id 
                ? 'rgba(255, 255, 255, 0.9)' 
                : 'rgba(255, 255, 255, 0.7)',
              color: activeSubcategory === subcategory.id 
                ? '#333' 
                : '#555',
              border: 'none',
              borderRadius: '20px',
              padding: '8px 16px',
              fontSize: '0.9rem',
              fontWeight: '600',
              cursor: 'pointer',
              transition: 'all 0.3s ease',
              textAlign: 'center'
            }}
            onMouseEnter={(e) => {
              if (activeSubcategory !== subcategory.id) {
                e.target.style.background = 'rgba(255, 255, 255, 0.9)';
                e.target.style.color = '#333';
              }
            }}
            onMouseLeave={(e) => {
              if (activeSubcategory !== subcategory.id) {
                e.target.style.background = 'rgba(255, 255, 255, 0.7)';
                e.target.style.color = '#555';
              }
            }}
          >
            {subcategory.name}
          </button>
        ))}
      </div>
    </div>
  );
};

export default SubcategoryTabs;
