import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useVideo } from '../contexts/VideoContext';
import VideoCard from '../components/VideoCard';
import { FaApple, FaLeaf, FaHeart, FaPencilAlt, FaChevronDown, FaPlay, FaBookmark } from 'react-icons/fa';

const Home = () => {
  const { user, isAuthenticated } = useAuth();
  const { 
    videos, 
    categories, 
    currentCategory, 
    loading, 
    error, 
    leaderboard,
    loadVideosByCategory,
    loadFeaturedVideos,
    clearError 
  } = useVideo();

  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [activeCategory, setActiveCategory] = useState('Workout & Sports');
  const [showSubcategories, setShowSubcategories] = useState({
    'Workout & Sports': true
  });

  const categoryData = [
    {
      name: 'Workout & Sports',
      color: '#FF8C42',
      subcategories: ['Pre - K - 2', 'Grades 3 - 4', 'Grades 5 - 8', 'HighSchool']
    },
    {
      name: 'Dance & Move', 
      color: '#4ECDC4',
      subcategories: ['Pre - K - 2', 'Grades 3 - 4', 'Grades 5 - 8', 'HighSchool']
    },
    {
      name: 'Yoga',
      color: '#96CEB4', 
      subcategories: ['Pre - K - 2', 'Grades 3 - 4', 'Grades 5 - 8', 'HighSchool']
    },
    {
      name: 'Mindfulness',
      color: '#45B7D1',
      subcategories: ['Pre - K - 2', 'Grades 3 - 4', 'Grades 5 - 8', 'HighSchool']
    },
    {
      name: 'Saved Videos',
      color: '#F7931E',
      subcategories: []
    }
  ];

  const toggleSubcategories = (categoryName) => {
    setShowSubcategories(prev => ({
      ...prev,
      [categoryName]: !prev[categoryName]
    }));
  };

  const handleCategoryClick = (categoryName) => {
    setActiveCategory(categoryName);
    toggleSubcategories(categoryName);
  };

  useEffect(() => {
    // Load featured videos on mount
    loadFeaturedVideos();
  }, []);

  const handleSubcategoryClick = (category, subcategory) => {
    console.log(`Loading videos for ${category} - ${subcategory}`);
    // This would filter videos by category and grade level
    loadVideosByCategory(category);
  };

  const handleVideoClick = (videoId) => {
    navigate(`/video/${videoId}`);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      console.log('Search:', searchQuery);
    }
  };

  if (error) {
    return (
      <div className="error">
        {error}
        <button onClick={clearError} className="btn btn-primary" style={{ marginLeft: '1rem' }}>
          Try Again
        </button>
      </div>
    );
  }

  return (
    <div className="fade-in">
      {/* Header with branding */}
      <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
        <h1>WELLNESS IN THE SCHOOLS</h1>
        <div style={{ display: 'flex', justifyContent: 'center', gap: '1rem', marginBottom: '1rem' }}>
          <FaApple style={{ color: '#FF6B35', fontSize: '2rem' }} />
          <FaLeaf style={{ color: '#4ECDC4', fontSize: '2rem' }} />
          <FaHeart style={{ color: '#45B7D1', fontSize: '2rem' }} />
          <FaPencilAlt style={{ color: '#96CEB4', fontSize: '2rem' }} />
        </div>
      </div>

      {/* Category Tabs */}
      <CategoryTabs 
        categories={categories}
        currentCategory={currentCategory}
        onCategoryChange={handleCategoryChange}
      />

      {/* Search Bar */}
      <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
        <form onSubmit={handleSearch} style={{ display: 'inline-block' }}>
          <input
            type="text"
            placeholder="Search videos..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="form-input"
            style={{ width: '300px', marginRight: '1rem' }}
          />
          <button type="submit" className="btn btn-secondary">
            Search
          </button>
        </form>
      </div>

      {/* Main Content */}
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 300px', gap: '2rem' }}>
        {/* Video Section */}
        <div>
          <h2>Videos of the Week!</h2>

          {loading ? (
            <div className="loading">Loading videos...</div>
          ) : videos.length > 0 ? (
            <div className="video-grid">
              {videos.map((video) => (
                <VideoCard
                  key={video._id}
                  video={video}
                  onClick={() => handleVideoClick(video._id)}
                />
              ))}
            </div>
          ) : (
            <div style={{ textAlign: 'center', padding: '2rem' }}>
              <p>No videos found for this category.</p>
            </div>
          )}
        </div>

        {/* Sidebar */}
        <div>
          {/* Leaderboard */}
          <Leaderboard leaderboard={leaderboard} />

          {/* User Stats (if authenticated) */}
          {isAuthenticated && user && (
            <div className="card">
              <h3>Your Stats</h3>
              <p>Total Views: {user.totalViews || 0}</p>
              <p>School: {user.schoolName}</p>
            </div>
          )}
        </div>
      </div>

      {/* Footer */}
      <div style={{ textAlign: 'center', marginTop: '3rem', padding: '2rem' }}>
        <h3>WELLNESS IN THE SCHOOLS</h3>
        <div style={{ display: 'flex', justifyContent: 'center', gap: '1rem' }}>
          <FaApple style={{ color: '#FF6B35', fontSize: '1.5rem' }} />
          <FaLeaf style={{ color: '#4ECDC4', fontSize: '1.5rem' }} />
          <FaHeart style={{ color: '#45B7D1', fontSize: '1.5rem' }} />
        </div>
      </div>
    </div>
  );
};

export default Home;
