import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useVideo } from '../contexts/VideoContext';
import { useAuth } from '../contexts/AuthContext';
import { FaArrowLeft, FaArrowRight, FaTimes, FaPlay } from 'react-icons/fa';

const VideoPlayer = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { currentVideo, getVideo, trackVideoView, videos } = useVideo();
  const { isAuthenticated } = useAuth();
  const [currentVideoIndex, setCurrentVideoIndex] = useState(0);

  useEffect(() => {
    const loadVideo = async () => {
      const video = await getVideo(id);
      if (video && isAuthenticated) {
        await trackVideoView(id);
      }
    };
    loadVideo();
  }, [id, isAuthenticated]);

  useEffect(() => {
    if (videos.length > 0) {
      const index = videos.findIndex(video => video._id === id);
      setCurrentVideoIndex(index >= 0 ? index : 0);
    }
  }, [id, videos]);

  const handleClose = () => {
    navigate('/');
  };

  const handlePrevious = () => {
    if (currentVideoIndex > 0) {
      const prevVideo = videos[currentVideoIndex - 1];
      navigate(`/video/${prevVideo._id}`);
    }
  };

  const handleNext = () => {
    if (currentVideoIndex < videos.length - 1) {
      const nextVideo = videos[currentVideoIndex + 1];
      navigate(`/video/${nextVideo._id}`);
    }
  };

  const getEmbedUrl = (driveUrl) => {
    const fileId = driveUrl.match(/\/d\/(.+?)(\/|$)/)?.[1];
    if (fileId) {
      return `https://drive.google.com/file/d/${fileId}/preview`;
    }
    return driveUrl;
  };

  if (!currentVideo) {
    return (
      <div className="loading">
        Loading video...
      </div>
    );
  }

  return (
    <div className="modal-overlay" style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, zIndex: 1000 }}>
      <div className="modal-content" style={{ 
        background: 'rgba(44, 62, 80, 0.95)', 
        backdropFilter: 'blur(20px)',
        borderRadius: '20px',
        padding: '2rem',
        maxWidth: '90vw',
        maxHeight: '90vh',
        overflow: 'hidden',
        border: '1px solid rgba(255, 255, 255, 0.2)',
        position: 'relative'
      }}>
        {/* Close Button */}
        <button
          onClick={handleClose}
          style={{
            position: 'absolute',
            top: '1rem',
            right: '1rem',
            background: 'rgba(231, 76, 60, 0.8)',
            border: 'none',
            borderRadius: '50%',
            width: '40px',
            height: '40px',
            color: 'white',
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 1001
          }}
        >
          <FaTimes />
        </button>

        {/* Navigation Buttons */}
        <button
          onClick={handlePrevious}
          disabled={currentVideoIndex === 0}
          style={{
            position: 'absolute',
            left: '1rem',
            top: '50%',
            transform: 'translateY(-50%)',
            background: currentVideoIndex === 0 ? 'rgba(255, 255, 255, 0.2)' : 'rgba(255, 107, 53, 0.8)',
            border: 'none',
            borderRadius: '50%',
            width: '50px',
            height: '50px',
            color: 'white',
            cursor: currentVideoIndex === 0 ? 'not-allowed' : 'pointer',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 1001
          }}
        >
          <FaArrowLeft />
        </button>

        <button
          onClick={handleNext}
          disabled={currentVideoIndex === videos.length - 1}
          style={{
            position: 'absolute',
            right: '1rem',
            top: '50%',
            transform: 'translateY(-50%)',
            background: currentVideoIndex === videos.length - 1 ? 'rgba(255, 255, 255, 0.2)' : 'rgba(255, 107, 53, 0.8)',
            border: 'none',
            borderRadius: '50%',
            width: '50px',
            height: '50px',
            color: 'white',
            cursor: currentVideoIndex === videos.length - 1 ? 'not-allowed' : 'pointer',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 1001
          }}
        >
          <FaArrowRight />
        </button>

        {/* Video Content */}
        <div style={{ textAlign: 'center' }}>
          <h2 style={{ marginBottom: '1rem', color: 'white' }}>{currentVideo.title}</h2>

          {/* Video Player */}
          <div style={{ 
            position: 'relative', 
            width: '100%', 
            maxWidth: '800px', 
            margin: '0 auto',
            background: '#000',
            borderRadius: '10px',
            overflow: 'hidden'
          }}>
            <iframe
              src={getEmbedUrl(currentVideo.googleDriveUrl)}
              width="100%"
              height="450"
              frameBorder="0"
              allowFullScreen
              title={currentVideo.title}
            />
          </div>

          {/* Video Info */}
          <div style={{ 
            marginTop: '1rem', 
            textAlign: 'left', 
            maxWidth: '800px', 
            margin: '1rem auto 0',
            color: 'white'
          }}>
            {currentVideo.description && (
              <p style={{ marginBottom: '1rem', lineHeight: '1.6' }}>
                {currentVideo.description}
              </p>
            )}

            <div style={{ display: 'flex', flexWrap: 'wrap', gap: '1rem', fontSize: '0.9rem' }}>
              {currentVideo.instructor && (
                <span style={{ color: 'rgba(255, 255, 255, 0.8)' }}>
                  Instructor: {currentVideo.instructor}
                </span>
              )}
              {currentVideo.duration && (
                <span style={{ color: 'rgba(255, 255, 255, 0.8)' }}>
                  Duration: {Math.floor(currentVideo.duration / 60)}:{(currentVideo.duration % 60).toString().padStart(2, '0')}
                </span>
              )}
              <span style={{ color: 'rgba(255, 255, 255, 0.8)' }}>
                Views: {currentVideo.viewCount || 0}
              </span>
              {currentVideo.difficulty && (
                <span style={{
                  background: currentVideo.difficulty === 'beginner' ? '#2ecc71' : 
                             currentVideo.difficulty === 'intermediate' ? '#f39c12' : '#e74c3c',
                  padding: '2px 8px',
                  borderRadius: '10px',
                  textTransform: 'capitalize'
                }}>
                  {currentVideo.difficulty}
                </span>
              )}
            </div>
          </div>

          {/* Video Navigation Info */}
          <div style={{ 
            marginTop: '1rem', 
            color: 'rgba(255, 255, 255, 0.7)', 
            fontSize: '0.9rem' 
          }}>
            {videos.length > 1 && (
              <p>
                Video {currentVideoIndex + 1} of {videos.length} â€¢ 
                Use arrow keys or click the arrows to navigate
              </p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default VideoPlayer;
