import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useVideo } from '../contexts/VideoContext';
import HorizontalVideoGrid from '../components/HorizontalVideoGrid';
import CategoryTabs from '../components/CategoryTabs';
import Leaderboard from '../components/Leaderboard';
import ChatBubble from '../components/ChatBubble';
import logo from '../img/logo.png';

const Home = () => {
  const { 
    videos, 
    categories, 
    currentCategory, 
    loading, 
    error, 
    leaderboard,
    loadVideosByCategory,
    loadVideosByCategoryAndSubcategory,
    loadFeaturedVideos,
    searchVideos,
    clearError 
  } = useVideo();

  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [activeSubcategory, setActiveSubcategory] = useState('pre-k-2-5min');
  const [showLeaderboard, setShowLeaderboard] = useState(false);

  // Fallback categories if the API fails
  const fallbackCategories = [
    { id: 'workout-sports', name: 'Workout & Sports', color: '#FF6B35' },
    { id: 'dance-move', name: 'Dance & Move', color: '#4ECDC4' },
    { id: 'yoga', name: 'Yoga', color: '#45B7D1' },
    { id: 'mindfulness', name: 'Mindfulness', color: '#8cc63e' }
  ];

  // Use fallback categories if the API categories are empty
  const displayCategories = categories && categories.length > 0 ? categories : fallbackCategories;

  useEffect(() => {
    // Always load featured videos on mount
    loadFeaturedVideos();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const handleCategoryChange = (category) => {
    if (category === 'featured') {
      loadFeaturedVideos();
      // Also need to update the current category in the context
      // This will be handled by the loadFeaturedVideos function
    } else {
      loadVideosByCategory(category);
    }
  };

  const handleVideoClick = (videoId) => {
    navigate(`/video/${videoId}`);
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      await searchVideos(searchQuery);
    } else {
      // If search is cleared, reload featured videos
      loadFeaturedVideos();
    }
  };

  const handleSubcategoryChange = (subcategory) => {
    setActiveSubcategory(subcategory);
    // Filter videos based on subcategory and duration
    if (currentCategory && currentCategory !== 'featured') {
      loadVideosByCategoryAndSubcategory(currentCategory, subcategory);
    }
  };

  if (error) {
    return (
      <div className="error">
        {error}
        <button onClick={clearError} className="btn btn-primary" style={{ marginLeft: '1rem' }}>
          Try Again
        </button>
      </div>
    );
  }

  return (
    <div className="fade-in">
      {/* Header with branding */}
      <div style={{ 
        textAlign: 'center', 
        marginTop: '2rem',
        marginBottom: '3rem',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        width: '100%'
      }}>
        <img 
          src={logo} 
          alt="Wellness in Schools Logo" 
          style={{ 
            height: '120px', 
            width: 'auto',
            filter: 'drop-shadow(0 4px 8px rgba(0,0,0,0.4))'
          }} 
        />
      </div>

      {/* Category Tabs and Leaderboard Toggle */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem', width: '100%' }}>
        <div style={{ flex: 1, width: '100%' }}>
          <CategoryTabs 
            categories={displayCategories}
            currentCategory={currentCategory}
            onCategoryChange={handleCategoryChange}
            activeSubcategory={activeSubcategory}
            onSubcategoryChange={handleSubcategoryChange}
          />
        </div>

        {/* Leaderboard Toggle Button */}
        <div style={{ marginLeft: '2rem' }}>
          <button
            onClick={() => setShowLeaderboard(!showLeaderboard)}
            className="btn btn-secondary"
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem',
              padding: '0.75rem 1.5rem',
              fontSize: '1rem',
              fontWeight: '600',
              borderRadius: '25px',
              border: '2px solid rgba(255, 255, 255, 0.3)',
              background: showLeaderboard 
                ? 'linear-gradient(45deg, #FF6B35, #FF8E53)' 
                : 'rgba(255, 255, 255, 0.1)',
              color: 'white',
              transition: 'all 0.3s ease',
              backdropFilter: 'blur(10px)'
            }}
            onMouseEnter={(e) => {
              if (!showLeaderboard) {
                e.target.style.background = 'rgba(255, 255, 255, 0.2)';
                e.target.style.transform = 'translateY(-2px)';
                e.target.style.boxShadow = '0 8px 25px rgba(255, 107, 53, 0.3)';
              }
            }}
            onMouseLeave={(e) => {
              if (!showLeaderboard) {
                e.target.style.background = 'rgba(255, 255, 255, 0.1)';
                e.target.style.transform = 'translateY(0)';
                e.target.style.boxShadow = 'none';
              }
            }}
          >
            üèÜ {showLeaderboard ? 'Hide Leaderboard' : 'Show Leaderboard'}
          </button>
        </div>
      </div>

      {/* Leaderboard - Only show when toggled */}
      {showLeaderboard && (
        <div style={{ marginBottom: '2rem' }}>
          <Leaderboard leaderboard={leaderboard} />
        </div>
      )}

      {/* Search Bar */}
      <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
        <form onSubmit={handleSearch} style={{ display: 'inline-block' }}>
          <input
            type="text"
            placeholder="Search videos..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="form-input"
            style={{ width: '300px', marginRight: '1rem' }}
          />
          <button type="submit" className="btn btn-secondary">
            Search
          </button>
          {searchQuery && (
            <button 
              type="button"
              onClick={() => {
                setSearchQuery('');
                loadFeaturedVideos();
              }}
              className="btn btn-danger"
              style={{ marginLeft: '0.5rem' }}
            >
              Clear
            </button>
          )}
        </form>
      </div>

      {/* Main Content - Centered */}
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <div style={{ maxWidth: '1200px', width: '100%' }}>
          <h2 style={{ textAlign: 'center', marginBottom: '2rem' }}>Videos of the Week!</h2>

          {loading ? (
            <div className="loading">Loading videos...</div>
          ) : (
            <HorizontalVideoGrid
              videos={videos}
              onVideoClick={handleVideoClick}
            />
          )}
        </div>
      </div>

      {/* Footer */}
      <div style={{ textAlign: 'center', marginTop: '3rem', padding: '2rem' }}>
        <h3>WELLNESS IN THE SCHOOLS</h3>
        <div style={{ display: 'flex', justifyContent: 'center', gap: '1rem' }}>
          <img 
            src={logo} 
            alt="Wellness in Schools Logo" 
            style={{ 
              height: '40px', 
              width: 'auto',
              filter: 'drop-shadow(0 2px 4px rgba(0,0,0,0.3))'
            }} 
          />
        </div>
      </div>

      {/* Chat Bubble */}
      <ChatBubble />
    </div>
  );
};

export default Home;
