import React, { useState } from 'react';
import { FaComments, FaTimes } from 'react-icons/fa';

const ChatBubble = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div style={{
      position: 'fixed',
      bottom: '2rem',
      right: '2rem',
      zIndex: 1000
    }}>
      {isOpen && (
        <div style={{
          position: 'absolute',
          bottom: '70px',
          right: '0',
          width: '300px',
          height: '400px',
          background: 'rgba(255, 255, 255, 0.95)',
          borderRadius: '15px',
          padding: '1rem',
          boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)',
          color: '#333'
        }}>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '1rem',
            paddingBottom: '0.5rem',
            borderBottom: '1px solid rgba(0, 0, 0, 0.1)'
          }}>
            <h3 style={{ margin: 0, color: '#4ECDC4' }}>Need Help?</h3>
            <button
              onClick={() => setIsOpen(false)}
              style={{
                background: 'none',
                border: 'none',
                fontSize: '1rem',
                cursor: 'pointer',
                color: '#666'
              }}
            >
              <FaTimes />
            </button>
          </div>

          <div style={{ height: '300px', overflowY: 'auto' }}>
            <div style={{
              background: '#f0f0f0',
              padding: '0.5rem',
              borderRadius: '10px',
              marginBottom: '0.5rem'
            }}>
              Hi! How can I help you with the Wellness in Schools platform?
            </div>

            <div style={{
              background: '#4ECDC4',
              color: 'white',
              padding: '0.5rem',
              borderRadius: '10px',
              marginBottom: '0.5rem',
              marginLeft: '2rem',
              textAlign: 'right'
            }}>
              How do I watch videos?
            </div>

            <div style={{
              background: '#f0f0f0',
              padding: '0.5rem',
              borderRadius: '10px',
              marginBottom: '0.5rem'
            }}>
              Simply click on any category tab above, then choose a subcategory for your age group, and click on any video to start watching!
            </div>
          </div>

          <div style={{
            position: 'absolute',
            bottom: '1rem',
            left: '1rem',
            right: '1rem'
          }}>
            <input
              type="text"
              placeholder="Type your message..."
              style={{
                width: '100%',
                padding: '0.5rem',
                border: '1px solid #ddd',
                borderRadius: '20px',
                outline: 'none'
              }}
            />
          </div>
        </div>
      )}

      <button
        onClick={() => setIsOpen(!isOpen)}
        style={{
          width: '60px',
          height: '60px',
          borderRadius: '50%',
          background: 'linear-gradient(45deg, #4ECDC4, #44A08D)',
          border: 'none',
          color: 'white',
          fontSize: '1.5rem',
          cursor: 'pointer',
          boxShadow: '0 4px 15px rgba(78, 205, 196, 0.4)',
          transition: 'all 0.3s ease',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center'
        }}
        onMouseEnter={(e) => {
          e.target.style.transform = 'scale(1.1)';
        }}
        onMouseLeave={(e) => {
          e.target.style.transform = 'scale(1)';
        }}
      >
        <FaComments />
      </button>

      {/* Chat label */}
      <div style={{
        position: 'absolute',
        top: '50%',
        right: '70px',
        transform: 'translateY(-50%)',
        background: '#4ECDC4',
        color: 'white',
        padding: '0.5rem 1rem',
        borderRadius: '20px',
        fontSize: '0.9rem',
        fontWeight: 'bold',
        whiteSpace: 'nowrap',
        opacity: isOpen ? 0 : 1,
        transition: 'opacity 0.3s ease'
      }}>
        Chat
      </div>
    </div>
  );
};

export default ChatBubble;
