import React, { useState, useEffect } from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

const PasswordRoute = ({ children }) => {
  const { passwordVerified } = useAuth();
  const [isProtectionEnabled, setIsProtectionEnabled] = useState(true);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkProtectionStatus = async () => {
      try {
        // Try to verify with empty password to check if protection is enabled
        // If protection is disabled, the API will return verified: true
        const res = await axios.post('/api/site-settings/verify-password', { password: '' });
        // If we get verified: true with empty password, protection is disabled
        // Otherwise, we need to check the actual status
        // For now, we'll check by trying to get settings (but that requires auth)
        // So we'll use a different approach - check if password is required
        setIsProtectionEnabled(true); // Default to enabled, will be checked on password page
      } catch (error) {
        // If error, assume protection is enabled
        setIsProtectionEnabled(true);
      } finally {
        setLoading(false);
      }
    };
    checkProtectionStatus();
  }, []);

  if (loading) {
    return null; // Or a loading spinner
  }

  // If password protection is disabled, allow access
  // We'll check this on the password protection page itself
  // For now, if password is not verified, redirect to password protection page
  if (!passwordVerified) {
    return <Navigate to="/password-protection" replace />;
  }

  // If password is verified, render the children (allow access to home page without authentication)
  return children;
};

export default PasswordRoute;
