import React, { useState, useEffect } from 'react';
import { FaUsers, FaVideo, FaChartBar, FaCog, FaPlus, FaEdit, FaTrash } from 'react-icons/fa';
import axios from 'axios';

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState('overview');
  const [loading, setLoading] = useState(false);
  const [videos, setVideos] = useState([]);
  const [showAddVideo, setShowAddVideo] = useState(false);
  const [showEditVideo, setShowEditVideo] = useState(false);
  const [editingVideo, setEditingVideo] = useState(null);
  const [newVideo, setNewVideo] = useState({
    title: '',
    description: '',
    category: 'workout-sports',
    subcategory: 'pre-k-2',
    googleDriveUrl: '',
    instructor: '',
    duration: 300, // 5 minutes in seconds
    tags: '',
    featured: false
  });

  const [editVideo, setEditVideo] = useState({
    title: '',
    description: '',
    category: 'workout-sports',
    subcategory: 'pre-k-2',
    googleDriveUrl: '',
    instructor: '',
    duration: 300, // 5 minutes in seconds
    tags: '',
    featured: false
  });

  const tabs = [
    { id: 'overview', name: 'Overview', icon: <FaChartBar /> },
    { id: 'users', name: 'Users', icon: <FaUsers /> },
    { id: 'videos', name: 'Videos', icon: <FaVideo /> },
    { id: 'settings', name: 'Settings', icon: <FaCog /> }
  ];

  const categories = [
    { id: 'workout-sports', name: 'Workout & Sports' },
    { id: 'dance-move', name: 'Dance & Move' },
    { id: 'yoga', name: 'Yoga' },
    { id: 'mindfulness', name: 'Mindfulness' }
  ];

  const subcategories = [
    { id: 'pre-k-2', name: 'Pre-K - 2' },
    { id: 'grades-3-4', name: 'Grades 3-4' },
    { id: 'grades-5-8', name: 'Grades 5-8' },
    { id: 'high-school', name: 'High School' }
  ];

  const durations = [
    { value: 300, label: '5 minutes' },
    { value: 600, label: '10 minutes' },
    { value: 900, label: '15 minutes' },
    { value: 1200, label: '20 minutes' }
  ];

  useEffect(() => {
    if (activeTab === 'videos') {
      loadVideos();
    }
  }, [activeTab]);

  const loadVideos = async () => {
    try {
      const response = await axios.get('/api/admin/videos');
      setVideos(response.data);
    } catch (error) {
      console.error('Error loading videos:', error);
    }
  };

  const handleAddVideo = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Extract Google Drive ID from URL
      const driveId = extractDriveId(newVideo.googleDriveUrl);

      const videoData = {
        ...newVideo,
        googleDriveId: driveId,
        tags: newVideo.tags.split(',').map(tag => tag.trim()).filter(tag => tag)
      };

      await axios.post('/api/admin/videos', videoData);
      setShowAddVideo(false);
      setNewVideo({
        title: '',
        description: '',
        category: 'workout-sports',
        subcategory: 'pre-k-2',
        googleDriveUrl: '',
        instructor: '',
        duration: 300,
        tags: '',
        featured: false
      });
      loadVideos();
    } catch (error) {
      console.error('Error adding video:', error);
    } finally {
      setLoading(false);
    }
  };

  const extractDriveId = (url) => {
    const match = url.match(/\/d\/(.+?)(\/|$)/);
    return match ? match[1] : url;
  };

  const deleteVideo = async (id) => {
    if (window.confirm('Are you sure you want to delete this video?')) {
      try {
        await axios.delete(`/api/admin/videos/${id}`);
        loadVideos();
      } catch (error) {
        console.error('Error deleting video:', error);
      }
    }
  };

  const startEditVideo = (video) => {
    setEditingVideo(video);
    setEditVideo({
      title: video.title,
      description: video.description || '',
      category: video.category,
      subcategory: video.subcategory || 'pre-k-2',
      googleDriveUrl: video.googleDriveUrl,
      instructor: video.instructor || '',
      duration: video.duration || 300,
      tags: video.tags ? video.tags.join(', ') : '',
      featured: video.featured || false
    });
    setShowEditVideo(true);
  };

  const handleEditVideo = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const videoData = {
        ...editVideo,
        tags: editVideo.tags.split(',').map(tag => tag.trim()).filter(tag => tag)
      };

      await axios.put(`/api/admin/videos/${editingVideo._id}`, videoData);
      setShowEditVideo(false);
      setEditingVideo(null);
      setEditVideo({
        title: '',
        description: '',
        category: 'workout-sports',
        subcategory: 'pre-k-2',
        googleDriveUrl: '',
        instructor: '',
        duration: 300,
        tags: '',
        featured: false
      });
      loadVideos();
    } catch (error) {
      console.error('Error updating video:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fade-in">
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
          <FaCog style={{ fontSize: '3rem', color: '#45B7D1', marginBottom: '1rem' }} />
          <h2>Admin Dashboard</h2>
          <p style={{ color: 'rgba(255, 255, 255, 0.7)' }}>
            Manage users, videos, and view analytics
          </p>
        </div>

        {/* Tab Navigation */}
        <div style={{ 
          display: 'flex', 
          gap: '1rem', 
          marginBottom: '2rem',
          flexWrap: 'wrap',
          justifyContent: 'center'
        }}>
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`btn ${activeTab === tab.id ? 'btn-success' : 'btn-secondary'}`}
              style={{ 
                display: 'flex', 
                alignItems: 'center', 
                gap: '0.5rem',
                padding: '12px 20px'
              }}
            >
              {tab.icon}
              {tab.name}
            </button>
          ))}
        </div>

        {/* Tab Content */}
        <div className="card">
          {activeTab === 'overview' && (
            <div>
              <h3>Dashboard Overview</h3>
