import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useVideo } from '../contexts/VideoContext';
import VideoCard from '../components/VideoCard';
import CategoryTabs from '../components/CategoryTabs';
import Leaderboard from '../components/Leaderboard';
import { FaApple, FaLeaf, FaHeart, FaPencilAlt } from 'react-icons/fa';

const Home = () => {
  const { user, isAuthenticated } = useAuth();
  const { 
    videos, 
    categories, 
    currentCategory, 
    loading, 
    error, 
    leaderboard,
    loadVideosByCategory,
    loadFeaturedVideos,
    clearError 
  } = useVideo();

  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [activeSubcategory, setActiveSubcategory] = useState('pre-k-2');

  useEffect(() => {
    // Load featured videos on mount
    if (currentCategory === 'featured') {
      loadFeaturedVideos();
    }
  }, []);

  const handleCategoryChange = (category) => {
    if (category === 'featured') {
      loadFeaturedVideos();
    } else {
      loadVideosByCategory(category);
    }
  };

  const handleVideoClick = (videoId) => {
    navigate(`/video/${videoId}`);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      // This would be implemented in the video context
      console.log('Search:', searchQuery);
    }
  };

  const handleSubcategoryChange = (subcategory) => {
    setActiveSubcategory(subcategory);
    // Filter videos based on subcategory
    console.log('Subcategory changed to:', subcategory);
  };

  if (error) {
    return (
      <div className="error">
        {error}
        <button onClick={clearError} className="btn btn-primary" style={{ marginLeft: '1rem' }}>
          Try Again
        </button>
      </div>
    );
  }

  return (
    <div className="fade-in">
      {/* Header with branding */}
      <div style={{ textAlign: 'center', marginBottom: '3rem' }}>
        <div style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center', 
          gap: '1rem', 
          marginBottom: '1rem',
          fontSize: '2.5rem',
          fontWeight: 'bold',
          letterSpacing: '2px'
        }}>
          <span style={{ color: 'white' }}>WELLNESS IN</span>
          <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
            {/* Apple Icon */}
            <div style={{
              width: '50px',
              height: '50px',
              backgroundColor: '#FF6B35',
              borderRadius: '50% 50% 50% 50% / 60% 60% 40% 40%',
              position: 'relative'
            }}>
              <div style={{
                width: '12px',
                height: '8px',
                backgroundColor: '#4a4a4a',
                position: 'absolute',
                top: '-3px',
                left: '50%',
                transform: 'translateX(-50%)',
                borderRadius: '0 0 50% 50%'
              }}></div>
            </div>

            {/* Leaf Icon */}
            <div style={{
              width: '50px',
              height: '50px',
              backgroundColor: '#4ECDC4',
              borderRadius: '0 100% 0 100%',
              transform: 'rotate(45deg)'
            }}></div>

            {/* Heart Icon */}
            <div style={{
              width: '50px',
              height: '50px',
              backgroundColor: '#96CEB4',
              position: 'relative',
              transform: 'rotate(-45deg)'
            }}>
              <div style={{
                width: '25px',
                height: '40px',
                backgroundColor: '#96CEB4',
                borderRadius: '25px 25px 0 0',
                position: 'absolute',
                left: '25px',
                transform: 'rotate(-45deg)',
                transformOrigin: '0 100%'
              }}></div>
              <div style={{
                width: '25px',
                height: '40px',
                backgroundColor: '#96CEB4',
                borderRadius: '25px 25px 0 0',
                position: 'absolute',
                left: '25px',
                transform: 'rotate(45deg)',
                transformOrigin: '0 100%'
              }}></div>
            </div>
          </div>
        </div>
        <div style={{ 
          fontSize: '2.5rem',
          fontWeight: 'bold',
          letterSpacing: '2px',
          color: 'white'
        }}>
          THE SCHOOLS
        </div>

        {/* Pencil underline */}
        <div style={{
          width: '200px',
          height: '8px',
          background: 'linear-gradient(90deg, #D2B48C 0%, #F4A460 50%, #DEB887 100%)',
          margin: '1rem auto',
          borderRadius: '4px',
          position: 'relative'
        }}>
          <div style={{
            width: '20px',
            height: '12px',
            background: 'linear-gradient(45deg, #D2B48C, #F4A460)',
            position: 'absolute',
            right: '-10px',
            top: '-2px',
            borderRadius: '0 50% 50% 0'
          }}></div>
        </div>
      </div>

      {/* Category Tabs */}
      <CategoryTabs 
        categories={categories}
        currentCategory={currentCategory}
        onCategoryChange={handleCategoryChange}
        activeSubcategory={activeSubcategory}
        onSubcategoryChange={handleSubcategoryChange}
      />

      {/* Search Bar */}
      <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
        <form onSubmit={handleSearch} style={{ display: 'inline-block' }}>
          <input
            type="text"
            placeholder="Search videos..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="form-input"
            style={{ width: '300px', marginRight: '1rem' }}
          />
          <button type="submit" className="btn btn-secondary">
            Search
          </button>
        </form>
      </div>

      {/* Main Content */}
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 300px', gap: '2rem' }}>
        {/* Video Section */}
        <div>
          <h2>Videos of the Week!</h2>

          {loading ? (
            <div className="loading">Loading videos...</div>
          ) : videos.length > 0 ? (
            <div className="video-grid">
              {videos.map((video) => (
                <VideoCard
                  key={video._id}
                  video={video}
                  onClick={() => handleVideoClick(video._id)}
                />
              ))}
            </div>
          ) : (
            <div style={{ textAlign: 'center', padding: '2rem' }}>
              <p>No videos found for this category.</p>
            </div>
          )}
        </div>

        {/* Sidebar */}
        <div>
          {/* Leaderboard */}
          <Leaderboard leaderboard={leaderboard} />

          {/* User Stats (if authenticated) */}
          {isAuthenticated && user && (
            <div className="card">
              <h3>Your Stats</h3>
              <p>Total Views: {user.totalViews || 0}</p>
              <p>School: {user.schoolName}</p>
            </div>
          )}
        </div>
      </div>

      {/* Footer */}
      <div style={{ textAlign: 'center', marginTop: '3rem', padding: '2rem' }}>
        <h3>WELLNESS IN THE SCHOOLS</h3>
        <div style={{ display: 'flex', justifyContent: 'center', gap: '1rem' }}>
          <FaApple style={{ color: '#FF6B35', fontSize: '1.5rem' }} />
          <FaLeaf style={{ color: '#4ECDC4', fontSize: '1.5rem' }} />
          <FaHeart style={{ color: '#45B7D1', fontSize: '1.5rem' }} />
        </div>
      </div>
    </div>
  );
};

export default Home;
