import React, { createContext, useContext, useReducer, useEffect } from 'react';
import axios from 'axios';

const VideoContext = createContext();

const initialState = {
  videos: [],
  categories: [],
  featuredVideos: [],
  currentCategory: 'featured',
  loading: false,
  error: null,
  leaderboard: [],
  currentVideo: null
};

const videoReducer = (state, action) => {
  switch (action.type) {
    case 'SET_LOADING':
      return {
        ...state,
        loading: action.payload
      };
    case 'SET_ERROR':
      return {
        ...state,
        error: action.payload,
        loading: false
      };
    case 'SET_CATEGORIES':
      return {
        ...state,
        categories: action.payload
      };
    case 'SET_VIDEOS':
      return {
        ...state,
        videos: action.payload,
        loading: false,
        error: null
      };
    case 'SET_FEATURED_VIDEOS':
      return {
        ...state,
        featuredVideos: action.payload
      };
    case 'SET_CURRENT_CATEGORY':
      return {
        ...state,
        currentCategory: action.payload
      };
    case 'SET_LEADERBOARD':
      return {
        ...state,
        leaderboard: action.payload
      };
    case 'SET_CURRENT_VIDEO':
      return {
        ...state,
        currentVideo: action.payload
      };
    case 'TRACK_VIDEO_VIEW':
      return {
        ...state,
        videos: state.videos.map(video =>
          video._id === action.payload
            ? { ...video, viewCount: video.viewCount + 1 }
            : video
        )
      };
    case 'CLEAR_ERROR':
      return {
        ...state,
        error: null
      };
    default:
      return state;
  }
};

export const VideoProvider = ({ children }) => {
  const [state, dispatch] = useReducer(videoReducer, initialState);

  // Load categories on mount
  useEffect(() => {
    loadCategories();
    loadFeaturedVideos();
    loadLeaderboard();
  }, []);

  // Load categories
  const loadCategories = async () => {
    try {
      const res = await axios.get('/api/videos/categories');
      dispatch({ type: 'SET_CATEGORIES', payload: res.data });
    } catch (error) {
      console.error('Error loading categories:', error);
    }
  };

  // Load videos by category
  const loadVideosByCategory = async (category) => {
    dispatch({ type: 'SET_LOADING', payload: true });
    try {
      const res = await axios.get(`/api/videos/category/${category}`);
      dispatch({ type: 'SET_VIDEOS', payload: res.data });
      dispatch({ type: 'SET_CURRENT_CATEGORY', payload: category });
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to load videos';
      dispatch({ type: 'SET_ERROR', payload: message });
    }
  };

  // Load featured videos
  const loadFeaturedVideos = async () => {
    try {
      const res = await axios.get('/api/videos/featured');
      dispatch({ type: 'SET_FEATURED_VIDEOS', payload: res.data });
    } catch (error) {
      console.error('Error loading featured videos:', error);
    }
  };

  // Load leaderboard
  const loadLeaderboard = async () => {
    try {
      const res = await axios.get('/api/videos/leaderboard/top?limit=10');
      dispatch({ type: 'SET_LEADERBOARD', payload: res.data });
    } catch (error) {
      console.error('Error loading leaderboard:', error);
    }
  };

  // Get single video
  const getVideo = async (id) => {
    dispatch({ type: 'SET_LOADING', payload: true });
    try {
      const res = await axios.get(`/api/videos/${id}`);
      dispatch({ type: 'SET_CURRENT_VIDEO', payload: res.data });
      dispatch({ type: 'SET_LOADING', payload: false });
      return res.data;
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to load video';
      dispatch({ type: 'SET_ERROR', payload: message });
      return null;
    }
  };

  // Track video view
  const trackVideoView = async (videoId) => {
    try {
      await axios.post(`/api/videos/${videoId}/view`);
      dispatch({ type: 'TRACK_VIDEO_VIEW', payload: videoId });
      // Reload leaderboard after view
      loadLeaderboard();
    } catch (error) {
      console.error('Error tracking video view:', error);
    }
  };

  // Search videos
  const searchVideos = async (query) => {
    if (!query.trim()) {
      return;
    }

    dispatch({ type: 'SET_LOADING', payload: true });
    try {
      const res = await axios.get(`/api/videos/search/${encodeURIComponent(query)}`);
      dispatch({ type: 'SET_VIDEOS', payload: res.data });
      dispatch({ type: 'SET_CURRENT_CATEGORY', payload: 'search' });
    } catch (error) {
      const message = error.response?.data?.message || 'Search failed';
      dispatch({ type: 'SET_ERROR', payload: message });
    }
  };

  // Clear error
  const clearError = () => {
    dispatch({ type: 'CLEAR_ERROR' });
  };

  // Get current videos based on category
  const getCurrentVideos = () => {
    if (state.currentCategory === 'featured') {
      return state.featuredVideos;
    }
    return state.videos;
  };

  const value = {
    videos: getCurrentVideos(),
    categories: state.categories,
    featuredVideos: state.featuredVideos,
    currentCategory: state.currentCategory,
    loading: state.loading,
    error: state.error,
    leaderboard: state.leaderboard,
    currentVideo: state.currentVideo,
    loadVideosByCategory,
    loadFeaturedVideos,
    loadLeaderboard,
    getVideo,
    trackVideoView,
    searchVideos,
    clearError
  };

  return (
    <VideoContext.Provider value={value}>
      {children}
    </VideoContext.Provider>
  );
};

export const useVideo = () => {
  const context = useContext(VideoContext);
  if (!context) {
    throw new Error('useVideo must be used within a VideoProvider');
  }
  return context;
};
