import React, { useState, useEffect } from 'react';
import { FaUsers, FaVideo, FaChartBar, FaCog, FaPlus, FaEdit, FaTrash } from 'react-icons/fa';
import axios from 'axios';

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState('overview');
  const [loading, setLoading] = useState(false);
  const [videos, setVideos] = useState([]);
  const [showAddVideo, setShowAddVideo] = useState(false);
  const [showEditVideo, setShowEditVideo] = useState(false);
  const [editingVideo, setEditingVideo] = useState(null);
  const [newVideo, setNewVideo] = useState({
    title: '',
    description: '',
    category: 'workout-sports',
    googleDriveUrl: '',
    instructor: '',
    ageGroup: 'all',
    tags: '',
    featured: false
  });

  const [editVideo, setEditVideo] = useState({
    title: '',
    description: '',
    category: 'workout-sports',
    googleDriveUrl: '',
    instructor: '',
    ageGroup: 'all',
    tags: '',
    featured: false
  });

  const tabs = [
    { id: 'overview', name: 'Overview', icon: <FaChartBar /> },
    { id: 'users', name: 'Users', icon: <FaUsers /> },
    { id: 'videos', name: 'Videos', icon: <FaVideo /> },
    { id: 'settings', name: 'Settings', icon: <FaCog /> }
  ];

  const categories = [
    { id: 'workout-sports', name: 'Workout & Sports' },
    { id: 'dance-move', name: 'Dance & Move' },
    { id: 'yoga', name: 'Yoga' },
    { id: 'mindfulness', name: 'Mindfulness' },
    { id: 'pre-school', name: 'Pre-School' },
    { id: 'k-12', name: 'K-12' }
  ];

  useEffect(() => {
    if (activeTab === 'videos') {
      loadVideos();
    }
  }, [activeTab]);

  const loadVideos = async () => {
    try {
      const response = await axios.get('/api/admin/videos');
      setVideos(response.data);
    } catch (error) {
      console.error('Error loading videos:', error);
    }
  };

  const handleAddVideo = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Extract Google Drive ID from URL
      const driveId = extractDriveId(newVideo.googleDriveUrl);

      const videoData = {
        ...newVideo,
        googleDriveId: driveId,
        tags: newVideo.tags.split(',').map(tag => tag.trim()).filter(tag => tag)
      };

      await axios.post('/api/admin/videos', videoData);
      setShowAddVideo(false);
      setNewVideo({
        title: '',
        description: '',
        category: 'workout-sports',
        googleDriveUrl: '',
        instructor: '',
        ageGroup: 'all',
        tags: '',
        featured: false
      });
      loadVideos();
    } catch (error) {
      console.error('Error adding video:', error);
    } finally {
      setLoading(false);
    }
  };

  const extractDriveId = (url) => {
    const match = url.match(/\/d\/(.+?)(\/|$)/);
    return match ? match[1] : url;
  };

  const deleteVideo = async (id) => {
    if (window.confirm('Are you sure you want to delete this video?')) {
      try {
        await axios.delete(`/api/admin/videos/${id}`);
        loadVideos();
      } catch (error) {
        console.error('Error deleting video:', error);
      }
    }
  };

  const startEditVideo = (video) => {
    setEditingVideo(video);
    setEditVideo({
      title: video.title,
      description: video.description || '',
      category: video.category,
      googleDriveUrl: video.googleDriveUrl,
      instructor: video.instructor || '',
      ageGroup: video.ageGroup || 'all',
      tags: video.tags ? video.tags.join(', ') : '',
      featured: video.featured || false
    });
    setShowEditVideo(true);
  };

  const handleEditVideo = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const videoData = {
        ...editVideo,
        tags: editVideo.tags.split(',').map(tag => tag.trim()).filter(tag => tag)
      };

      await axios.put(`/api/admin/videos/${editingVideo._id}`, videoData);
      setShowEditVideo(false);
      setEditingVideo(null);
      setEditVideo({
        title: '',
        description: '',
        category: 'workout-sports',
        googleDriveUrl: '',
        instructor: '',
        ageGroup: 'all',
        tags: '',
        featured: false
      });
      loadVideos();
    } catch (error) {
      console.error('Error updating video:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fade-in">
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
          <FaCog style={{ fontSize: '3rem', color: '#45B7D1', marginBottom: '1rem' }} />
          <h2>Admin Dashboard</h2>
          <p style={{ color: 'rgba(255, 255, 255, 0.7)' }}>
            Manage users, videos, and view analytics
          </p>
        </div>

        {/* Tab Navigation */}
        <div style={{ 
          display: 'flex', 
          gap: '1rem', 
          marginBottom: '2rem',
          flexWrap: 'wrap',
          justifyContent: 'center'
        }}>
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`btn ${activeTab === tab.id ? 'btn-success' : 'btn-secondary'}`}
              style={{ 
                display: 'flex', 
                alignItems: 'center', 
                gap: '0.5rem',
                padding: '12px 20px'
              }}
            >
              {tab.icon}
              {tab.name}
            </button>
          ))}
        </div>

        {/* Tab Content */}
        <div className="card">
          {activeTab === 'overview' && (
            <div>
              <h3>Dashboard Overview</h3>
              <p>Welcome to the admin dashboard! This is where you can manage all aspects of the Wellness in Schools platform.</p>

              <div style={{ 
                display: 'grid', 
                gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', 
                gap: '1rem', 
                marginTop: '2rem' 
              }}>
                <div className="card" style={{ textAlign: 'center' }}>
                  <FaUsers style={{ fontSize: '2rem', color: '#FF6B35', marginBottom: '1rem' }} />
                  <h4>Total Users</h4>
                  <p style={{ fontSize: '2rem', fontWeight: 'bold', color: '#FF6B35' }}>0</p>
                </div>

                <div className="card" style={{ textAlign: 'center' }}>
                  <FaVideo style={{ fontSize: '2rem', color: '#4ECDC4', marginBottom: '1rem' }} />
                  <h4>Total Videos</h4>
                  <p style={{ fontSize: '2rem', fontWeight: 'bold', color: '#4ECDC4' }}>{videos.length}</p>
                </div>

                <div className="card" style={{ textAlign: 'center' }}>
                  <FaChartBar style={{ fontSize: '2rem', color: '#45B7D1', marginBottom: '1rem' }} />
                  <h4>Total Views</h4>
                  <p style={{ fontSize: '2rem', fontWeight: 'bold', color: '#45B7D1' }}>0</p>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'users' && (
            <div>
              <h3>User Management</h3>
              <p>Manage user accounts, view statistics, and monitor activity.</p>
              <div style={{ textAlign: 'center', padding: '2rem', color: 'rgba(255, 255, 255, 0.7)' }}>
                <FaUsers style={{ fontSize: '3rem', marginBottom: '1rem' }} />
                <p>User management features coming soon...</p>
              </div>
            </div>
          )}

          {activeTab === 'videos' && (
            <div>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
                <h3>Video Management</h3>
                <button
                  onClick={() => setShowAddVideo(true)}
                  className="btn btn-primary"
                  style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}
                >
                  <FaPlus />
                  Add Video
                </button>
              </div>

              {showAddVideo && (
                <div className="card" style={{ marginBottom: '2rem' }}>
                  <h4>Add New Video</h4>
                  <form onSubmit={handleAddVideo}>
                    <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
                      <div className="form-group">
                        <label className="form-label">Title</label>
                        <input
                          type="text"
                          value={newVideo.title}
                          onChange={(e) => setNewVideo({...newVideo, title: e.target.value})}
                          className="form-input"
                          required
                        />
                      </div>

                      <div className="form-group">
                        <label className="form-label">Category</label>
                        <select
                          value={newVideo.category}
                          onChange={(e) => setNewVideo({...newVideo, category: e.target.value})}
                          className="form-input"
                          required
                        >
                          {categories.map(cat => (
                            <option key={cat.id} value={cat.id}>{cat.name}</option>
                          ))}
                        </select>
                      </div>

                      <div className="form-group">
                        <label className="form-label">Google Drive URL</label>
                        <input
                          type="url"
                          value={newVideo.googleDriveUrl}
                          onChange={(e) => setNewVideo({...newVideo, googleDriveUrl: e.target.value})}
                          className="form-input"
                          placeholder="https://drive.google.com/file/d/..."
                          required
                        />
                      </div>

                      <div className="form-group">
                        <label className="form-label">Instructor</label>
                        <input
                          type="text"
                          value={newVideo.instructor}
                          onChange={(e) => setNewVideo({...newVideo, instructor: e.target.value})}
                          className="form-input"
                        />
                      </div>


                      <div className="form-group">
                        <label className="form-label">Age Group</label>
                        <select
                          value={newVideo.ageGroup}
                          onChange={(e) => setNewVideo({...newVideo, ageGroup: e.target.value})}
                          className="form-input"
                        >
                          <option value="all">All Ages</option>
                          <option value="pre-school">Pre-School</option>
                          <option value="elementary">Elementary</option>
                          <option value="middle">Middle School</option>
                          <option value="high">High School</option>
                        </select>
                      </div>
                    </div>

                    <div className="form-group">
                      <label className="form-label">Description</label>
                      <textarea
                        value={newVideo.description}
                        onChange={(e) => setNewVideo({...newVideo, description: e.target.value})}
                        className="form-input"
                        rows="3"
                      />
                    </div>

                    <div className="form-group">
                      <label className="form-label">Tags (comma-separated)</label>
                      <input
                        type="text"
                        value={newVideo.tags}
                        onChange={(e) => setNewVideo({...newVideo, tags: e.target.value})}
                        className="form-input"
                        placeholder="workout, morning, energy"
                      />
                    </div>

                    <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
                      <label style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                        <input
                          type="checkbox"
                          checked={newVideo.featured}
                          onChange={(e) => setNewVideo({...newVideo, featured: e.target.checked})}
                        />
                        Featured Video
                      </label>
                    </div>

                    <div style={{ display: 'flex', gap: '1rem', marginTop: '1rem' }}>
                      <button type="submit" className="btn btn-primary" disabled={loading}>
                        {loading ? 'Adding...' : 'Add Video'}
                      </button>
                      <button
                        type="button"
                        onClick={() => setShowAddVideo(false)}
                        className="btn btn-secondary"
                      >
                        Cancel
                      </button>
                    </div>
                  </form>
                </div>
              )}

              <div>
                <h4>Current Videos ({videos.length})</h4>
                {videos.length > 0 ? (
                  <div style={{ display: 'grid', gap: '1rem' }}>
                    {videos.map((video) => (
                      <div key={video._id} className="card" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <div>
                          <h5>{video.title}</h5>
                          <p style={{ color: 'rgba(255, 255, 255, 0.7)', marginBottom: '0.5rem' }}>
                            {video.category}  {video.instructor}
                          </p>
                          <p style={{ fontSize: '0.9rem', color: 'rgba(255, 255, 255, 0.6)' }}>
                            {video.description}
                          </p>
                        </div>
                        <div style={{ display: 'flex', gap: '0.5rem' }}>
                          <button
                            onClick={() => deleteVideo(video._id)}
                            className="btn btn-danger"
                            style={{ padding: '8px 12px' }}
                          >
                            <FaTrash />
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div style={{ textAlign: 'center', padding: '2rem', color: 'rgba(255, 255, 255, 0.7)' }}>
                    <FaVideo style={{ fontSize: '3rem', marginBottom: '1rem' }} />
                    <p>No videos found. Add your first video!</p>
                  </div>
                )}
              </div>
            </div>
          )}

          {activeTab === 'settings' && (
            <div>
              <h3>Platform Settings</h3>
              <p>Configure platform settings and preferences.</p>
              <div style={{ textAlign: 'center', padding: '2rem', color: 'rgba(255, 255, 255, 0.7)' }}>
                <FaCog style={{ fontSize: '3rem', marginBottom: '1rem' }} />
                <p>Settings configuration coming soon...</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
